import { Component, OnInit, ElementRef, ViewChild} from '@angular/core';
import { GoogleMap, Marker} from "capacitor/google"
import { ModalController } from "@ionic/angular";
import { environment } from "src/environments/environment";
import { ModalPage } from "../modal/modal.page";
import { from } from 'rxjs';

@Component({
  selector: 'app-lobby',
  templateUrl: './lobby.page.html',
  styleUrls: ['./lobby.page.scss'],
})
export class LobbyPage {
  @ViewChild('map')mapRef!: ElementRef;
  map!: GoogleMap;

  constructor(private modalCtrl:ModalController) {}


  ionViewDidEnter(){
    this.createMap();
  }


  async createMap(){
    this.map = await GoogleMap.create({
    id: 'my-map',
    apiKey: environment.mapsKey,
    element: this.mapRef.nativeElement,
    config: {
      center: {
        lat:-33.04353334085918,
        lng:-71.60435289558721,

      },
      zoom: 14,
    }
    });
    await this.addMarkers();
    
  }

  async addMarkers(){
    const markers: Marker[] =[
    {
      coordinate:{
        lat:33.7,
        lng:-117.8,
      },
      title: 'localhost',
      snippet:'Best place on earth',
      },
      {
        coordinate: {
          lat:33.7,
          lng:-117.8,
        },
        title: 'random place',
      snippet:'not sure',
      },
    
    ];

    const result = await this.map.addMarkers(markers);
   

    this.map.setOnMarkerClickListener(async (marker) =>{
      const modal = await this.modalCtrl.create({
        component: ModalPage,
        componentProps: {
        marker,},
        breakpoints:[0, 0.3],
        initialBreakpoint: 0.3,
       
      
      });
      modal.present();


    });
  }
}
  


<ion-menu contentId="main-content" >
  <ion-header>
    <ion-toolbar>
      <ion-title>DuocExpress </ion-title>
    </ion-toolbar>
  </ion-header>



  <ion-content class="ion-padding">
    <ion-item routerLink="/exit">
      <ion-icon name="home-outline" slot="start"></ion-icon>
      Menu Principal
    </ion-item>
    <ion-item routerLink="/home">
      <ion-icon name="person-outline" slot="start"></ion-icon>
      Perfil
    </ion-item>
    
    <ion-item routerLink="/start">
      <ion-icon name="car-outline" slot="start"></ion-icon>
      Registrate como conductor
    </ion-item>
    
    <ion-item routerLink="/chatbox">
      <ion-icon name="chatbox-outline" slot="start"></ion-icon>
      Contactos
    </ion-item>

    <ion-item routerLink="/card">
      <ion-icon name="card-outline" slot="start"></ion-icon>
      Pagos
    </ion-item>

    <ion-item routerLink="/settings">
      <ion-icon name="settings-outline" slot="start"></ion-icon>
      Configuración
    </ion-item>


    
    <ion-item id="cerrar" routerLink="/home">
      <ion-icon name="exit-outline" slot="start"></ion-icon>
      Cerrar sesion
    </ion-item> 
  
    </ion-content>
</ion-menu>
<div class="ion-page" id="main-content">
  <ion-header>
    <ion-toolbar>
      <ion-buttons slot="start">
        <ion-menu-button></ion-menu-button>
      </ion-buttons>
      <ion-title>DuocExpress</ion-title>
    </ion-toolbar>
  </ion-header>
  <ion-content class="ion-padding"> 
    <ion-content>
      <div class="container">
        <capacitor-google-map #map></capacitor-google-map>
        </div>

        
    </ion-content>
  </ion-content>
</div>
